INSERT INTO `posts` VALUES (3,1,1,4,2,'Credit Policy Manual','credit-policy-manual-1490867070','<p><strong>VERSION 3, March 2016</strong></p>\r\n\r\n<p><strong>Revised by:&nbsp;</strong>Ernest Mbidha</p>\r\n\r\n<p><strong>Reviewed by:&nbsp;</strong>Pearson Njeru, Rosemary Muyeshi &amp; James Onyutta</p>\r\n\r\n<p><strong>&nbsp;</strong></p>\r\n\r\n<table style=\"width:682px\">\r\n	<tbody>\r\n		<tr>\r\n			<td colspan=\"5\">\r\n			<p><strong>MUSONI CREDIT POLICY MANUAL</strong></p>\r\n\r\n			<p><strong>Amendment Table</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Date</strong></td>\r\n			<td><strong>Chapter/Section</strong></td>\r\n			<td><strong>Description of Amendment</strong></td>\r\n			<td><strong>&nbsp;Update By</strong></td>\r\n			<td><strong>Version</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>26.10.2010</td>\r\n			<td>All</td>\r\n			<td>Reviewed the Policy</td>\r\n			<td>Norbert Benker</td>\r\n			<td>October 2010</td>\r\n		</tr>\r\n		<tr>\r\n			<td>30.10.2014</td>\r\n			<td>All</td>\r\n			<td>Split of policies and procedures</td>\r\n			<td>Stanly Munyao</td>\r\n			<td>January 2014</td>\r\n		</tr>\r\n		<tr>\r\n			<td>26.01.2016</td>\r\n			<td>All</td>\r\n			<td>Consolidated the Policy to one document<br />\r\n			Review to capture use of tablet and changes in the product offering</td>\r\n			<td>Ernest Mbidha</td>\r\n			<td>March 2016</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table style=\"width:684px\">\r\n	<tbody>\r\n		<tr>\r\n			<td colspan=\"2\"><strong>Table of Abbreviations</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Abbreviation</strong></td>\r\n			<td><strong>Explanation</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>ABM</td>\r\n			<td>Assistant Branch Manager (approved position)</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Ag BM</td>\r\n			<td>Acting Branch Manager (temporary assignment)</td>\r\n		</tr>\r\n		<tr>\r\n			<td>BM</td>\r\n			<td>Branch Managers</td>\r\n		</tr>\r\n		<tr>\r\n			<td>CAM</td>\r\n			<td>Credit Admin Manager